#include "ntgcalls.h"

int main() {
    uint32_t uid = ntg_init();
    ... // Your Code Here

    int result = ntg_destroy(uid);
    if (result != 0) {
        printf("NTgCalls cleanup failed.\n");
        return 1;
    }
    return 0;
}

#include "ntgcalls.h"
