#include <ntgcalls.h>
#include <mutex>

void onCallReady(void* data) {
    ((std::mutex*)data)->unlock();
}

void makeAsyncCall() {
    ntg_async_struct asyncData;
    std::mutex mtx;
    asyncData.userData = &mtx;
    asyncData.promise = onCallReady;
    mutex.lock();
    int error = ntg_pause(asyncData);
    if (error != 0) {
        // Handle error
    }
    mutex.lock();
    // Async operation completed
    if (asyncData.errorCode != 0) {
        // Handle Async error
    }
}

#include <ntgcalls.h>
#include <mutex>
#include <iostream>

struct CustomData {
    std::mutex* mtx;
    int64_t chatID;
};

void onCallReady(void* data) {
    CustomData* customData = (CustomData*)data;
    customData->mtx->unlock();
    std::cout << "Chat ID: " << customData->chatID << std::endl;
}

void makeAsyncCall() {
    ntg_async_struct asyncData;
    std::mutex mtx;
    CustomData data;
    data.mtx = &mtx;
    data.chatID = 1234567890;
    asyncData.userData = &data;
    asyncData.promise = onCallReady;
    mutex.lock();
    int error = ntg_pause(asyncData);
    if (error != 0) {
        // Handle error
    }
    mutex.lock();
    // Async operation completed
    if (asyncData.errorCode != 0) {
        // Handle Async error
    }
}
